# summary
python << END
import os
samples = [x for x in os.listdir("NOVOPlasty-animal-mitogenome-eval") if x.endswith(".csv")]
with open("NOVOPlasty-animal-mitogenome-eval/summary.tab", "w") as output_h:
    for s in samples:
        with open(os.path.join("NOVOPlasty-animal-mitogenome-eval", s)) as input_h:
            head = input_h.readline()
            genes = []
            for line in input_h:
                if line.strip():
                    genes.extend(line.split("\t")[2].split(","))
            genes = sorted(set(genes))
            output_h.write(s.replace(".csv", "") + "\t" + str(len(genes)) + "\t" + ",".join(genes) + "\n")
END

